{
  // code block to be executed
}
while (condition);

sections.example:
int i = 0;
do 
{
  Console.WriteLine(i);
  i++;
}
while (i < 5);

sections.syntax:
for (statement 1; statement 2; statement 3) 
{
  // code block to be executed
}

sections.example:
for (int i = 0; i < 5; i++) 
{
  Console.WriteLine(i);
}

for (int i = 0; i <= 10; i = i + 2) 
{
  Console.WriteLine(i);
}

// Outer loop
for (int i = 1; i <= 2; ++i) 
{
  Console.WriteLine("Outer: " + i);  // Executes 2 times

  // Inner loop
  for (int j = 1; j <= 3; j++) 
  {
    Console.WriteLine(" Inner: " + j); // Executes 6 times (2 * 3)
  }
}

for (int i = 0; i < 10; i++) 
{
  if (i == 4) 
  {
    break;
  }
  Console.WriteLine(i);
}

for (int i = 0; i < 10; i++) 
{
  if (i == 4) 
  {
    continue;
  }
  Console.WriteLine(i);
}

int i = 0;
while (i < 10) 
{
  Console.WriteLine(i);
  i++;
  if (i == 4) 
  {
    break;
  }
}

int i = 0;
while (i < 10) 
{
  if (i == 4) 
  {
    i++;
    continue;
  }
  Console.WriteLine(i);
  i++;
}

